console.log("E-Commerce Website Loaded");const hamburger=document.getElementById("hamburger"),navLinks=document.querySelector(".nav-links");hamburger.addEventListener("click",()=>{navLinks.classList.toggle("active")});const productGrid=document.getElementById("productGrid"),loadingMessage=document.createElement("p");loadingMessage.textContent="Loading products...",loadingMessage.style.fontSize="18px",loadingMessage.style.margin="20px 0",productGrid.appendChild(loadingMessage),fetch("https://fakestoreapi.com/products").then(e=>{if(!e.ok)throw new Error("Network response was not OK");return e.json()}).then(e=>{productGrid.innerHTML="",e.forEach(e=>{const t=document.createElement("div");t.className="product-card",t.innerHTML=`<a href="product.html?id=${e.id}" style="text-decoration:none; color:inherit;"><img loading="lazy" src="${e.image}" alt="${e.title}" /><h3>${e.title}</h3><p>$${e.price}</p></a><button onclick="addToCart(${e.id})">Add to Cart</button>`,productGrid.appendChild(t)})}).catch(e=>{console.error("Error loading products:",e),productGrid.innerHTML='<p style="color: red;">⚠️ Failed to load products. Please try again later.</p>'});function addToCart(e){let t=JSON.parse(localStorage.getItem("cart"))||[];t.push(e),localStorage.setItem("cart",JSON.stringify(t)),updateCartCount(),alert("Product added to cart!")}function updateCartCount(){const e=JSON.parse(localStorage.getItem("cart"))||[],t=document.querySelector(".cart-icon .badge");t&&(t.textContent=e.length)}updateCartCount();
